import React from 'react'
import './Person.css'
import Radium from 'radium';

const person=(props)=>{
   const buttonStyle={
        backgroundColor:'green',
        color:'white',
        font:'inherit',
        border:'1px solid blue',
        padding:'8px',
        cursor:'pointer',
        ':hover':{
            backgroundColor: 'lightGreen',
            color:'black'
        }
   };
   return(
       <div >
           <p>I am {props.name} and I am {props.age} years old</p>
           <p>{props.children}</p>
           <button  style={buttonStyle} key={btn1} onClick={props.clickToChange}>Click to change name</button>
           <br></br>
           <button style={buttonStyle} key={btn2} onClick={props.clickToDelete}>Click to Delete Person</button>
           <br></br>
           <input type="text" onChange={props.changed} value={props.name}/>
       </div>
   );
};

export default Radium(person);
//export default person;
